import { useEffect } from "react";
import { useState } from "react";
import Card from "../components/Card";

const Photos = () => {
  const url = "http://localhost:3001/photos";
  const [params, setParams] = useState(url);
  const [photos, setPhotos] = useState([]);
  const [sort, setSort] = useState("desc");
  const [submited, setSubmited] = useState("");
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const deletePhoto = async (id) => {
    const config = {
      method: "DELETE",
  };

  await fetch(url + `/${id}`, config);
  const res = await fetch(url);
  const data = await res.json();
  const expectedCaptions = data
    .filter((photo) => photo.id !== 10)
    .map((photo) => photo);
  setPhotos(await expectedCaptions);
  };

  const fetchPhotos = useCallback(async () => {
  setLoading(true);
  try {
    const res = await fetch(params);
    const data = await res.json();
    setPhotos(await data);
  } catch (e) {
    setError(e.message);
  }
  setLoading(false);
  },[params]);

  useEffect(() => {
  fetchPhotos();
  },[fetchPhotos, submited, sort]);

  if (error) return <h1 style={{ width: "100%", textAlign: "center", marginTop: "20px" }} >Error!</h1>;

  return (
    <>
      <div className="container">
        <div className="options">
          <select
            onChange={(e) => setSort(e.target.value)}
            data-testid="sort"
            className="form-select"
            style={{}}
          >
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              setSubmited(search);
            }}
          >
            <input
              type="text"
              data-testid="search"
              onChange={(e) => setSearch(e.target.value)}
              className="form-input"
            />
            <input
              type="submit"
              value="Search"
              data-testid="submit"
              className="form-btn"
            />
          </form>
        </div>
        <div className="content">
          {loading ? (
            <h1
              style={{ width: "100%", textAlign: "center", marginTop: "20px" }}
            >
              Loading...
            </h1>
          ) : (
            photos.map((photo) => {
              return (
                <Card key={photo.id} photo={photo} deletePhoto={deletePhoto} />
              );
            })
          )}
        </div>
      </div>
    </>
  );
};

export default Photos;
